import React, { useState } from 'react';
// import Products from './components/Products';
// import Box from './components/Box/Box';
// import Home from './components/Main/NavBar'
// import Product from './components/Product';
import dataFake from './dataFake';
import Routers from './routers'
function App() {

  const [products, setProducts] = useState(dataFake);
  const onHandleRemove = (id) => {
    const newProducts = products.filter(product => product.id !== id);
    const confirmDelete = window.confirm('Ban co chac chan xoa hay khong ?')
    if(confirmDelete) setProducts(newProducts);
  }
  // const onHandleAdd = (product)=>{
  //   const newProduct = {
  //     id : products.length + 1,
  //     ... product
  //   }
  //   console.log(products);
  //   setProducts([
  //     ...products,
  //     newProduct
  //   ])

  // }
  useEffect(() => {
    const getProducts = async () => {
      try {
        const { data } = await apiRequest.getAll();
        setProducts(data);
      } catch (error) {
        console.log('failed to request API: ', error)
      }
    }
    getProducts();
  }, []);
  const onHandleAdd = async (product) => {
    try {
      const { data } = await apiRequest.create(product);
      setProducts([
        ...products,
        data
      ])
    } catch (error) {
      console.log('failed to request API: ', error)
    }
  }
  return (
    <div className="App">
      <Routers products={products} onRemove={onHandleRemove} onAdd={onHandleAdd}  />
    </div>
  )

}
export default App;
