import React from 'react'
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import LayoutMain from '../pages/layouts/LayoutMain'
import LayoutAdmin from '../pages/layouts/LayoutAdmin'
//Admin
import Dashboard from '../pages/views/Admin/Dashboard'
import ProductsManager from '../pages/views/Admin/Products'
import AddProduct from '../pages/views/Admin/AddProduct';

//Views
import About from '../pages/views/Main/About'
import Home from '../pages/views/Main/Home'



const Routers = ({ products, onRemove,onAdd }) => {
    const onHandleRemove = (id) => {
        onRemove(id)
    }
    const onHandleAdd = (product)=>{
        const newProduct = {
          id : products.length+1,
          ... product
        }
        console.log(products);
        setProducts([
          ...products,
          newProduct
        ])
        const [valueInput, setValueInput] = useState({});
        const onHandleChange = (e) => {
            const { name, value } = e.target;
            setValueInput({
                ...valueInput,
                [name]: value
            })
        }
        const onHandleSubmit = (e) => {
            e.preventDefault();
            onAdd(valueInput);
        }
      }
    return (
        <Router>
            <Switch>
                <Route path="/admin/:path?" exact>
                    <LayoutAdmin>
                        <Switch>
                            <Route path='/admin' exact>
                                <Dashboard />
                            </Route>
                            <Route path='/admin/products'>
                                <ProductsManager products={products} onRemove={onHandleRemove} />
                            </Route>
                            <Route path='/admin/addproducts'>
                            <AddProduct products={products} onAdd={onHandleAdd} />
                            </Route>
                        </Switch>
                    </LayoutAdmin>
                </Route>
                <Route>
                    <LayoutMain>
                        <Switch>
                            <Route path="/" exact>
                                <Home products={products} />
                            </Route>
                            <Route path="/about">
                                <About />
                            </Route>
                        </Switch>
                    </LayoutMain>
                </Route>
            </Switch>
        </Router>
    )
}

Routers.propTypes = {

}

export default Routers
