import React, { useState } from 'react'
import PropTypes from 'prop-types'

const AddProduct = ({onAdd}) => {
    const [valueInput,setValueInput]=useState({});
    const onHandleChange = (e)=>{
        const {name,value}= e.target;
        setValueInput({
            ...valueInput,
            [name] : value
        })
    }
    const onHandleSubmit = (e)=>{
        e.preventDefault();
        onAdd(valueInput);
    }
    return (
        <div>
                       <ol className="breadcrumb">
              <li className="breadcrumb-item"><a href="index.html">Home</a><i className="fa fa-angle-right" />Add Product</li>
            </ol> 
        <div className="card shadow mb-4">
            <div className="card-header py-3">
                <h6 className="m-0 font-weight-bold text-primary">Add Product</h6>
            </div>
            <div className="card-body">
                <form onSubmit={onHandleSubmit}> 
                    <div className="form-group">
                        <label htmlFor="inputProductName">Product Name</label>
                        <input type="text" className="form-control" name="name" id="inputProductName" onChange={onHandleChange}></input>
                    </div>
                    <div className="form-group">
                        <label htmlFor="inputProductImage">Product Image</label>
                        <input type="text" className="form-control" name="image" id="inputProductImage" onChange={onHandleChange}></input>
                    </div>
                    <div className="form-group">
                        <label htmlFor="inputProductPrice">Product Price</label>
                        <input type="text" className="form-control" name="price" id="inputProductPrice" onChange={onHandleChange}></input>
                    </div>
                    <button type="submit" className="btn btn-primary">Submit</button>
                </form>
            </div>
        </div>
        </div>
    )
}

AddProduct.propTypes = {

}

export default AddProduct
